import { BrowserRouter, Routes, Route } from "react-router-dom";
import Login from "../screens/auth/login";
import Signup from "../screens/auth/signup";
import Verifymail from "../screens/auth/verifymail";
import ForgetPassword from "../screens/auth/forgetpassword";
import ResetPassword from "../screens/auth/resetpassword";
import PrivateRoutes from "../components/protectedRoute/Privateroute";
import { useSelector, useDispatch } from "react-redux";
import React, { useEffect } from "react";
import { setUser } from "../screens/redux/authSlice";
import Setting from "../screens/settings/Setting";
import PrivacyPolicy from "../screens/privacyPolicy/PrivacyPolicy";
import Properties from "../screens/properties/Properties";
import NotFound from "../screens/errorScreen/NotFound";
import AddProperty from "../screens/addProperty";
import FinalReports from "../screens/finalReports/FinalReports";
import { Auth } from "../services/Auth";

function Router() {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    if (token) {
      getProfile();
    }
  }, [token]);

  async function getProfile() {
    Auth.GetProfile(token)
      .then(({ data }) => {
        dispatch(setUser(data?.profile));
      })
      .catch((error) => {
        //  toast.error("Something went wrong", error);
      });
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route element={<PrivateRoutes />}>
          <Route path="/properties" element={<Properties />} />
          <Route path="/addProperty" element={<AddProperty />} />
          <Route path="/settings" element={<Setting />} />
          <Route path="/finalReports" element={<FinalReports />} />
        </Route>
        <Route path="/" element={<Login />} />
        <Route path="/privacypolicy" element={<PrivacyPolicy />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/verifymail" element={<Verifymail />} />
        <Route path="/forgetpassword" element={<ForgetPassword />} />
        <Route path="/404" element={<NotFound />} />
        <Route path="/email-verification/:token" element={<ResetPassword />} />
      </Routes>
    </BrowserRouter>
  );
}

export default Router;
