import React from "react";
import Dashboard from "../dashBoard";
import SideBar from "../../components/sideBar";

const Products = () => {
  const navigate = useNavigate();
  const { token } = useSelector((state) => state.auth);
  const [record, setRecord] = useState(false);
  const [page, setPage] = useState(0);
  const [limit, setLimit] = useState(10);
  const [refreshTable, setRefreshTable] = useState(null);

  const handlePageClick = (data) => {
    setPage(data.selected);
  };
  async function fetch_properties() {
    Property.FetchProperties(token, page, limit)
      .then((res) => {
        setRecord(res.data);
      })
      .catch((error) => {
        toast.error(serviceErrorHandler(error));
      });
  }

  useEffect(() => {
    fetch_properties();
  }, [page, refreshTable]);
  return (
    <div>
      <SideBar>

        <div >
          <div >
            <h3 className="title">Calculate your estate's worth</h3>
          </div>
          <div className="addPropertyDiv">
            {!record?.data ? (
              <>
                <Loader color={"black"} width={"50"} height={"50"} />
              </>
            ) : (
              <>
                <div className="tableDiv">
                  <div className="table">
                    <PropertiesTable
                      data={record?.data}
                      totalPages={record?.totalPages}
                      setRefreshTable={setRefreshTable}
                    />
                    <Pagination
                      handlePageClick={handlePageClick}
                      totalPages={record?.totalPages}
                    />
                  </div>

                </div>
              </>
            )}
          </div>
        </div>
      </SideBar>
    </div>
  );
};

export default Products;
